var EmailAutoPopulate = Class.create();
EmailAutoPopulate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getEmailAutopopulate: function() {
		var userObj={};
		var gr=GlideRecord('cmn_location');
		if(gr.get(this.getParameter('sysparm_userSelect'))){
			userObj.locstreet=gr.getValue("street");
			userObj.locname=gr.getValue("name");
			userObj.loccity=gr.getValue("city");
			userObj.loczip=gr.getValue("zip");
			userObj.locstate=gr.getValue("state");
			userObj.loccountry=gr.getValue("country");
		}
		return JSON.stringify(userObj);
//      var userObj={};
// 		var gr= GlideRecord('sys_user');
// 		if(gr.get(this.getParameter('sysparm_userSelect'))){
// 			userObj.mail=gr.getValue('email');
// 		}
//        return JSON.stringify(userObj);
    },
    type: 'EmailAutoPopulate'
});
