sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values older
// then the current time.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var VeryoverdueNITasks = new GlideRecord('x_58872_needit_needit_task');
VeryoverdueNITasks.addQuery('due_date','<=',rightNow);
VeryoverdueNITasks.addQuery('due_date','<',gs.hoursAgo(72));
VeryoverdueNITasks.addQuery('state','!=',3);

VeryoverdueNITasks.query();
// Write a log message for each overdue NeedIt Task record
// Generate an event for each overdue NeedIt Task record
while(VeryoverdueNITasks.next()){
	gs.info("(3)Very Overdue NeedIt Task record = " + VeryoverdueNITasks.number+" "+VeryoverdueNITasks.short_description+" "+VeryoverdueNITasks.due_date);

}
